mapper.select:
SELECT t.ownerUserName, t.stepName, t.arriveTime, t.submitUserId, t.actionName FROM checkList c RIGHT JOIN instanceRelation r ON c.id = r.checkId RIGHT JOIN flowActionTrace t ON t.instanceId = r.instanceId
c.recordNo = #{recordNo}
UNION SELECT t.ownerUserName, t.stepName, t.arriveTime, t.submitUserId, t.actionName FROM flowInstance i RIGHT JOIN flowActionTrace t ON i.id = t.instanceId JOIN instanceAndDelay r ON t.instanceId = r.instanceId RIGHT JOIN checkAndDanger cd ON cd.delayToApplyForId = r.delayToApplyId RIGHT JOIN checkList c ON c.id = cd.checkId JOIN dangerList d ON d.id = cd.dangerId JOIN delayToApplyFor daf ON daf.id = r.delayToApplyId
daf.delayToApplyForNo = #{recordNo}
order by arriveTime desc
